              .sort((a, b) => {
                if (a.toLowerCase() > b.toLowerCase()) return 1;
                if (a.toLowerCase() < b.toLowerCase()) return -1;
                return 0;
              })
              .map((widget) => {
                const fullWidgetName = `${folder}.${widget}`;
                return (
                  <a
                    href={`#/${NDCUI_ACCOUNT}/widget/index?widget=${fullWidgetName}`}
                    onClick={() => {
                      State.update({
                        widget: fullWidgetName,
                      });
                    }}
                    className={state.widget === fullWidgetName ? "active" : ""}
                  >
                    {widget}
                  </a>
                );
              })}
          </div>
        );
      })}
      {Object.keys(otherItems).map((folder) => {
        return (
          <div>
            <h3>{folder}</h3>
            {otherItems[folder].map((item) => {
              return (
                <a href={item.link} {...item.otherProps}>
                  {item.name}
                </a>
              );
            })}
          </div>
        );
      })}
    </Sidebar>
    {tabContent()}
    <Widget src={`${NDCUI_ACCOUNT}/widget/Typography.OpenSansFont`} />
  </Wrapper>
);

