  if (page < creators.length - 1) {
    setPage(page + 1);
  } else {
    setPage(0);
  }
};
const HandleCreatorDownSlide = () => {
  if (page > 0) {
    setPage(page - 1);
  } else {
    setPage(creators.length - 1);
  }
};
const HandleDeveloperUpSlide = () => {
  if (devPage < developers.length - 1) {
    setDevPage(devPage + 1);
  } else {
    setDevPage(0);
  }
};
const HandleDeveloperDownSlide = () => {
  if (devPage > 0) {
