const TransferOption = ({ onClose, data, isDarkModeOn }) => {
  const [tokens, setTokens] = useState([]);
  const [accountIds, setAccountIds] = useState([
    {
      accountId: "",
      tokenAmount: "",
    },
  ]);
  function fetchNFTDetails() {
    asyncFetch("https://graph.mintbase.xyz", {
      method: "POST",
      headers: {
        Accept: "*/*",
        "Content-Type": "application/json",
        "mb-api-key": "omni-site",
        "x-hasura-role": "anonymous",
      },
      body: JSON.stringify({
        query: `  
        query v2_omnisite_GetTokenByThingID($thingId: String!, $limit: Int, $offset: Int, $search_fields: [mb_views_nft_tokens_bool_exp!]) {
          token: mb_views_nft_tokens(
            where: {metadata_id: {_eq: $thingId}, burned_timestamp: {_is_null: true}, _or: $search_fields}
            limit: $limit
            offset: $offset
          ) {
            id: token_id
            ownerId: owner
          }

        }
        `,
        variables: {
          limit: 50,
          offset: 0,
          thingId: data?.metadata_id,
          search_fields: {
            owner: {
              _eq: data?.owner,
            },
          },
        },
      }),
    }).then((data) => {
      if (data?.body?.data) {
        setTokens(data?.body?.data?.token?.map((data) => data?.id));
      }
    });
  }
  useEffect(() => {
    fetchNFTDetails();
  }, []);
  const handleCheckAccount = () => {
    fetch("https://rpc.mainnet.near.org", {
      method: "POST",
      headers: {
        Accept: "*/*",
        "Content-Type": "application/json",
        "Access-Control-Allow-Credentials": true,
      },
      body: JSON.stringify({
        id: "dontcare2",
        jsonrpc: "2.0",
        method: "query",
        params: {
          request_type: "view_account",
          finality: "final",
          account_id: "account",
        },
      }),
    });
  };
  const addAccounts = () => {
    setAccountIds([
      ...accountIds,
      {
        tokenAmount: "",
        accountId: "",
      },
    ]);
  };
  const handleTransfer = () => {
    let transfers = [];
    if (accountIds.length === 1 && accountIds[0].tokenAmount === "1") {
      nftTransfer(
        data?.token_id,
        accountIds[0]?.accountId,
        data?.nft_contract_id
      );
    } else {
      // Batch transfer
      let k = 0;
      for (let i = 0; i < accountIds.length; i++) {
        const { accountId, tokenAmount } = accountIds[i];
        for (let j = 0; j < parseInt(tokenAmount); j++) {
          const tokenId = tokens[k] || "";
          k++;
          transfers.push([tokenId, accountId]);
        }
      }
      nftTransfer(data?.token_id, transfers, data?.nft_contract_id);
    }
  };
  const totalTokenAmount = accountIds.reduce((total, data) => {
    return total + parseInt(data.tokenAmount || 0);
  }, 0);
  const handleAccountName = (e, index, name) => {
    const newFields = accountIds;
    newFields[index][name] = e.target.value;
    setAccountIds(newFields);
  };
  const handleRemoveCard = (id) => {
    const existingFields = accountIds?.filter((_, index) => index !== id);
    setAccountIds(existingFields);
  };
  return (
    <TransferContainer>
      <div className={isDarkModeOn ? "home-dark" : "home-light"}>
        <Top isDarkModeOn={isDarkModeOn}>
          <p>Transfer</p>
          <p onClick={onClose}>X</p>
        </Top>
        <Token isDarkModeOn={isDarkModeOn}>
          {tokens?.length === 1 ? (
            <p>Token: {tokens[0]}</p>
          ) : (
            <p>Multiple Tokens</p>
          )}
        </Token>
        <Content>
          <div className={isDarkModeOn ? "text-dark" : "text-white"}>
            <p>Airdrop to multiple accounts</p>
            <p>Amount of tokens and recipient account</p>
          </div>
          <Cards>
            <div className={isDarkModeOn ? "dark" : "light"}>
              {accountIds.map((data, index) => (
                <div key={index} className="accountCard">
                  <input
                    className="count"
                    type="number"
                    maxLength={2}
                    max={tokens?.length - data?.amount}
                    onChange={(e) => handleAccountName(e, index, "tokenAmount")}
                    value={data?.tokenAmount}
                  />
                  <input
                    value={data?.accountId}
                    onChange={(e) => handleAccountName(e, index, "accountId")}
                    className="account"
                    placeholder="account.near"
                  />
                  <div
                    style={{ cursor: "pointer" }}
                    onClick={() =>
                      accountIds.length > 1 ? handleRemoveCard(index) : {}
                    }
                  >
                    {cancelSvg}
                  </div>
                </div>
              ))}
              <div
                onClick={addAccounts}
                className={isDarkModeOn ? "add-dark" : "add-light"}
              >
                Add Another Account
              </div>
            </div>
          </Cards>
        </Content>
        <Bottom isDarkModeOn={isDarkModeOn}>
          <div>
            <div className="token">
              <span>Available Tokens</span>
              <p>{Math.max(tokens.length - totalTokenAmount, 0)}</p>
            </div>
            <div className="token">
              <span>Available Tokens</span>
              <p>{tokens?.length}</p>
            </div>
          </div>
          <div className="clear">
            <p
              onClick={() =>
                setAccountIds([
                  {
                    accountId: "",
                    tokenAmount: "",
                  },
                ])
              }
            >
              Clear all
            </p>
            <button
              disabled={
                accountIds.some(
                  (data) => data.accountId === "" || data.tokenAmount === ""
                ) || tokens.length < totalTokenAmount
              }
              onClick={handleTransfer}
            >
              {" "}
              Continue
            </button>
          </div>
        </Bottom>
      </div>
    </TransferContainer>
  );
};
return <TransferOption {...props} />;

bos.genadrop.near/widget/Mintbase.NFT.mo.code:
const LISTING_DEPOSIT = `1000${"0".repeat(18)}`;
const GAS = "200000000000000";
const MARKET_CONTRACT_ADDRESS = {
  mainnet: "simple.market.mintbase1.near",
  testnet: "market-v2-beta.mintspace2.testnet",
};
const USDC_ADDRESS = {
  mainnet: "a0b86991c6218b36c1d19d4a2e9eb0ce3606eb48.factory.bridge.near",
  testnet: "usdc.fakes.testnet",
};
const USDT_ADDRESS = {
  mainnet: "dac17f958d2ee523a2206206994597c13d831ec7.factory.bridge.near",
  testnet: "usdt.fakes.testnet",
};
const ftContracts = {
  usdt: USDT_ADDRESS,
  usdc: USDC_ADDRESS,
};
const _price = (price) =>
  Number(Number(new Big(price).mul(new Big(10).pow(24)).toString()))
    .toLocaleString()
    .replace(/,/g, "");
const nftTransfer = (tokenId, accountIds, contractName) => {
  const deposit = 1;
  if (typeof accountIds !== "string") {
    return Near.call([
      {
        contractName,
        methodName: "nft_batch_transfer",
        args: {
          token_ids: accountIds,
        },
        deposit,
        gas: GAS,
      },
    ]);
  }
  try {
    return Near.call([
      {
        contractName,
        methodName: "nft_transfer",
        args: {
          token_id: tokenId,
          receiver_id: accountIds,
        },
        deposit,
      },
    ]);
  } catch (err) {
    console.log(err);
  }
};
const listNFT = (contractAddress, tokenIds, mainnet, price, ft) => {
  if (!contractAddress) return;
  if (tokenIds.length < 1) return;
  const gas = 2e14;
  const storageDeposit = 1e22;
  const msg = { price: _price(price) };
  if (ft) {
    const ftContractId = ftContracts[ft].mainnet;
    msg.ft_contract = ftContractId;
    msg.price = Number(price) * 1000000;
  }
  const ids = tokenIds.map((data) => ({
    contractName: contractAddress,
    args: {
      token_id: data,
      account_id: mainnet
        ? MARKET_CONTRACT_ADDRESS.mainnet
        : MARKET_CONTRACT_ADDRESS.testnet,
      msg: JSON.stringify(msg),
    },
    methodName: "nft_approve",
    deposit: LISTING_DEPOSIT,
    gas: GAS,
  }));
  try {
    return Near.call([
      {
        contractName: mainnet
          ? MARKET_CONTRACT_ADDRESS.mainnet
          : MARKET_CONTRACT_ADDRESS.testnet,
        methodName: "deposit_storage",
        args: {},
        gas: gas,
        deposit: storageDeposit,
      },
      ...ids,
    ]);
  } catch (error) {
    console.log(error);
  }
};
const delist = (contractAddress, tokenIds, mainnet, oldMarket) => {
  if (!tokenIds.length) return;
  const ids = tokenIds.map((data) => ({
    contractName: mainnet
      ? MARKET_CONTRACT_ADDRESS.mainnet
      : MARKET_CONTRACT_ADDRESS.testnet,
    methodName: "unlist",
    gas: GAS,
    deposit: `1`,
    args: {
      token_ids: [data],
      nft_contract_id: contractAddress,
    },
  }));
  try {
    return Near.call([...ids]);
  } catch (error) {
    console.log(error);
  }
};
const burnNFT = (contractAddress, tokenIds, mainnet) => {
  if (!tokenIds.length) return;
  try {
    return Near.call([
      {
        contractName: contractAddress,
        methodName: "nft_batch_burn",
        gas: GAS,
        deposit: `1`,
        args: {
          token_ids: tokenIds,
        },
      },
    ]);
  } catch (error) {
    console.log(error);
  }
};
const multiplyNFT = (
  contractAddress,
  ownerId,
  reference,
  media,
  numberToMint
) => {
  try {
    return Near.call([
      {
        contractName: contractAddress,
        methodName: "nft_batch_mint",
        gas: GAS,
        deposit: `1`,
        args: {
          owner_id: ownerId,
          metadata: {
            reference: reference,
            media: media,
          },
          num_to_mint: numberToMint,
          royalty_args: null,
          token_ids_to_mint: null,
          split_owners: null,
        },
      },
    ]);
  } catch (error) {
    console.log(error);
  }
};
return {
  nftTransfer,
  listNFT,
  delist,
  burnNFT,
  multiplyNFT,
};

