let { onLoad, onRefresh, loaded } = props;
const spec = "nft-1.0.0";
const base_uri = "https://arweave.net";
const isSignedin = !!context.accountId;
// const marketAddress = "simple.market.mintbase1.near";
const _price = (price) =>
  Number(Number(new Big(price).mul(new Big(10).pow(24)).toString()))
    .toLocaleString()
    .replace(/,/g, "");
let MintbaseSDK = {
  initialized: false,
  mainnet: props.mainnet ? true : false,
  factoryAddress: MintbaseSDK.mainnet ? "mintbase1.near" : "mintspace2.testnet",
  contractName: props.contractName || "",
  marketAddress: MintbaseSDK.mainnet
    ? "simple.market.mintbase1.near"
    : "market-v2-beta.mintspace2.testnet",
  owner_id: context.accountId,
  mbGraphEndpoin: `https://graph.mintbase.xyz`,
  init: () => {
    MintbaseSDK.initialized = true;
    MintbaseSDK.refresh();
  },
  refresh: () => {
    if (onRefresh) {
      onRefresh(MintbaseSDK);
    }
  },
  ipfsUrl: (cid) => `https://ipfs.near.social/ipfs/${cid}`,
  getTokenById: (contractName, tokenId) => {
    if (!contractName || !tokenId)
      return console.log("missing contract name or token id");
    const res = asyncFetch(MintbaseSDK.mbGraphEndpoin, {
      method: "POST",
      headers: {
        "mb-api-key": "anon",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        query: `
        query GetTokenById(
          $tokenId: String!
          $contractAddress: String!
        ) {
          mb_views_nft_tokens(
            where: {
              nft_contract_id: { _eq: $contractAddress }
              token_id: { _eq: $tokenId }
            }
            limit: 1
          ) {
            baseUri: base_uri
            burned_receipt_id
            burned_timestamp
            copies
            description
            expires_at
            issued_at
            last_transfer_receipt_id
            last_transfer_timestamp
            media
            minter
            media_hash
            mint_memo
            nft_contract_is_mintbase
            minted_receipt_id
            minted_timestamp
            metadata_id
            document: reference_blob(path: "$.document")
            animationUrl: reference_blob(path: "$.animation_url")
            extra: reference_blob(path: "$.extra")
            reference
            reference_hash
            starts_at
            title
            updated_at
            owner
            royalties
            royalties_percent
            token_id
          }

          mb_views_active_listings_aggregate(
            where: {
              token_id: { _eq: $tokenId }
              kind: { _eq: "auction" }
              nft_contract_id: { _eq: $contractAddress }
            }
          ) {
            aggregate {
              count
            }
          }
        }
        `,
        variables: {
          tokenId: tokenId,
          contractAddress: contractName || MintbaseSDK.contractName,
        },
      }),
    });
    return res;
  },
  getStoreNfts: (contractName) => {
    if (!contractName) return console.log("missing contract name");
    try {
      const response = asyncFetch(MintbaseSDK.mbGraphEndpoin, {
        method: "POST",
        headers: {
          "mb-api-key": "anon",
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          query: `query GetStoreNfts( 
          $offset: Int = 0 $condition: mb_views_nft_metadata_unburned_bool_exp ) 
          @cached 
          { mb_views_nft_metadata_unburned( where: $condition 
            offset: $offset order_by: { minted_timestamp: desc } ) 
           { createdAt: minted_timestamp 
             listed: price 
             media 
             storeId: nft_contract_id 
             metadataId: metadata_id 
             title base_uri 
           } 
          mb_views_nft_metadata_unburned_aggregate(where: $condition) 
          { 
            aggregate { 
              count 
            } 
           } 
         }
      `,
          variables: {
            condition: {
              nft_contract_id: {
                _in: contractName || MintbaseSDK.contractName,
              },
            },
          },
        }),
      });
      return response;
    } catch (err) {
      console.log(err);
    }
  },
  getOwnedNFTs: (owner) => {
    if (!owner) return console.log("missing owner address");
    try {
      const response = asyncFetch(MintbaseSDK.mbGraphEndpoin, {
        method: "POST",
        headers: {
          "mb-api-key": "anon",
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          query: `query MyQuery($owner: String!) {
                  mb_views_nft_tokens(
                    where: {owner: {_eq: $owner}, _and: {burned_timestamp: {}, last_transfer_timestamp: {}}}
                    limit: 30
                    order_by: {minted_timestamp: asc}
                  ) {
                    nft_contract_id
                    title
                    description
                    media
                    last_transfer_receipt_id
                  }
                }`,
          variables: {
            owner: owner || MintbaseSDK.owner_id,
          },
        }),
      });
      return response;
    } catch (err) {
      console.log(err);
    }
  },
  deployStore: (storeName, symbol_name, reference, referenceHash) => {
    const gas = 2e14;
    const deposit = 65e23;
    if (!isSignedin) return console.log("sign in first");
    if (!storeName || !symbol_name) {
      return console.log("missing store name or symbol");
    }
    try {
      return Near.call([
        {
          contractName: MintbaseSDK.factoryAddress,
          methodName: "create_store",
          args: {
            owner_id: MintbaseSDK.owner_id,
            metadata: {
              name: storeName,
              spec: spec,
              symbol: symbol_name,
              base_uri,
              ...(reference && { reference }),
              ...(referenceHash && { reference_hash: referenceHash }),
            },
          },
          deposit: deposit,
          gas: gas,
        },
      ]);
    } catch (err) {
      console.log(err);
    }
  },
  mint: (tokenMetadata, media, contractName, numToMint) => {
    if (!isSignedin) return console.log("sign in first");
    if (!media) return console.log("missing file");
    asyncFetch("https://ipfs.near.social/add", {
      method: "POST",
      headers: {
        Accept: "application/json",
      },
      body: media,
    })
      .then((res) => {
        const cid = res.body.cid;
        const gas = 2e14;
        return Near.call([
          {
            contractName: contractName || MintbaseSDK.contractName,
            methodName: "nft_batch_mint",
            args: {
              owner_id: MintbaseSDK.owner_id,
              metadata: {
                media: MintbaseSDK.ipfsUrl(cid),
                ...tokenMetadata,
              },
              num_to_mint: numToMint || 1,
              royalty_args: {
                split_between: {
                  [MintbaseSDK.owner_id]: 10000,
                },
                percentage: 1000,
              },
              split_owners: null,
            },
            gas: gas,
            deposit: 1e22,
          },
        ]);
      })
      .catch((err) => console.log(err));
  },
  nftBurn: (tokenIds, contractName) => {
    if (!isSignedin) return console.log("sign in first");
    if (!tokenIds.length) return console.log("missing token ids");
    const gas = 2e14;
    const deposit = 1;
    try {
      return Near.call([
        {
          contractName: contractName || MintbaseSDK.contractName,
          methodName: "nft_batch_burn",
          args: {
            token_ids: tokenIds,
          },
          gas,
          deposit,
        },
      ]);
    } catch (err) {
      console.log(err);
    }
  },
  nftTransfer: (tokenId, accountId, contractName) => {
    if (!isSignedin) return console.log("sign in first");
    if (!tokenId || !accountId)
      return console.log("token id or receiver address is missing");
    const deposit = 1;
    try {
      return Near.call([
        {
          contractName: contractName || MintbaseSDK.contractName,
          methodName: "nft_transfer",
          args: {
            token_id: tokenId,
            receiver_id: accountId,
          },
          deposit,
        },
      ]);
    } catch (err) {
      console.log(err);
    }
  },
  nftApprove: (tokenId, contractName, price) => {
    if (!isSignedin) return console.log("sign in first");
    if (!tokenId || !price > 0)
      return console.log("token id or price is missing");
    const gas = 2e14;
    const storageDeposit = 1e22;
    return Near.call([
      {
        contractName: MintbaseSDK.marketAddress,
        methodName: "deposit_storage",
        args: {},
        gas: gas,
        deposit: storageDeposit,
      },
      {
        methodName: "nft_approve",
        contractName: contractName || MintbaseSDK.contractName,
        gas: gas,
        args: {
          token_id: tokenId,
          account_id: MintbaseSDK.marketAddress,
          msg: JSON.stringify({
            price: _price(price),
          }),
        },
        deposit: 8e22,
      },
    ]);
  },
};
if (onLoad && !loaded) {
  MintbaseSDK.init();
  onLoad(MintbaseSDK);
}

bos.genadrop.near/widget/Mintbase.TableP.code:
const TablePagination = ({
  onPageChange,
  currentPage,
  itemsPerPage,
  totalItems,
  hideLabel,
  isDarkModeOn,
}) => {
  const DOTS = "...";
  const range = (start, end) => {
    const length = end - start + 1;
    return Array.from({ length }, (_, idx) => idx + start);
  };
  const [paginationRange, setPaginationRange] = useState([]);
  const totalPageCount = Math.ceil(totalItems / itemsPerPage);
  useMemo(() => {
    const totalPageNumbers = 6;
    if (totalPageNumbers >= totalPageCount) {
      setPaginationRange(range(1, totalPageCount));
    }
    if (totalPageCount <= 5) return;
    const leftSiblingIndex = Math.max(currentPage - 1, 1);
    const rightSiblingIndex = Math.min(currentPage + 1, totalPageCount);
    const shouldShowLeftDots = leftSiblingIndex > 1;
    const shouldShowRightDots = rightSiblingIndex < totalPageCount - 1;
    const firstPageIndex = 1;
    const lastPageIndex = totalPageCount;
    if (!shouldShowLeftDots && shouldShowRightDots) {
      const leftItemCount = 3;
      const leftRange = range(1, leftItemCount);
      setPaginationRange([...leftRange, DOTS, totalPageCount]);
    }
    if (shouldShowLeftDots && !shouldShowRightDots) {
      const rightItemCount = 3;
      const rightRange = range(
        totalPageCount - rightItemCount + 1,
        totalPageCount
      );
      setPaginationRange([firstPageIndex, DOTS, ...rightRange]);
    }
    if (shouldShowLeftDots && shouldShowRightDots) {
      const middleRange = range(leftSiblingIndex, rightSiblingIndex);
      setPaginationRange([
        firstPageIndex,
        DOTS,
        ...middleRange,
        DOTS,
        lastPageIndex,
      ]);
    }
  }, [totalItems, itemsPerPage, currentPage]);
  const nextPage = () => {
    if (currentPage === totalPageCount) return;
    onPageChange(currentPage + 1);
  };
  const previousPage = () => {
    if (currentPage === 1) return;
    onPageChange(currentPage - 1);
  };
  const showingCount = Math.min(props.itemsPerPage, props.totalItems) || 0;
  const PaginationRoot = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    padding: 1rem 0;
    @media only screen and (max-width: 768px) {
      flex-direction: column-reverse;
      padding: 1rem 0;
      gap: 1rem;
    }
    .previous {
      display: flex;
      align-items: center;
      cursor: ${currentPage === 1 ? "not-allowed" : "pointer"};
    }
    .forward {
      display: flex;
      align-items: center;
      cursor: ${currentPage === totalPageCount ? "not-allowed" : "pointer"};
    }
    .list {
      display: flex;
      padding-left: 1rem;
      padding-right: 1rem;
      justify-content: center;
      align-items: center;
      color: #93c5fd;
      margin-bottom: 0;
      @media (max-width: 768px) {
        padding-left: 6rem;
        padding-right: 6rem;
        margin-left: 3rem;
      }
    }
    .pagination {
      display: flex;
      flex-direction: row;
      align-items: center;
      justify-content: center;
    }
    .page-number {
      flex: 1;
      display: flex;
      align-items: center;
      justify-content: center;
      border-radius: 0.25rem;
      width: 2rem;
      height: 2rem;
      margin: 0 0.25rem;
      border: 1px solid transparent;
      color: ${isDarkModeOn ? "#9CA3AF" : "#93C5FD"};
      background-color: ${isDarkModeOn ? "#1F2937" : "#EFF6FF"};
      transition-property: all;
      transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
      transition-duration: 300ms;
      transition-duration: 500ms;
      cursor: pointer;
      &:hover {
        background-color: ${isDarkModeOn ? "#374151" : "#DBEAFE"};
      }
      &:focus {
        outline: none;
        box-shadow: 0 0 0 2px ${isDarkModeOn ? "#93C5FD" : "#3B82F6"};
      }
      &.active {
        background-color: ${isDarkModeOn ? "#B91C1C" : "#F87171"};
        color: ${isDarkModeOn ? "#FFF" : "#FFF"};
        border: 1px solid transparent;
        box-shadow: none;
      }
    }
    .showingCount {
      padding-top: 1rem;
      font-weight: 500;
      text-align: center;
      color: ${isDarkModeOn ? "#fff" : "#eeeee"};
      flex: 1;
    }
  `;
  return (
    <PaginationRoot>
      {!hideLabel && (
        <div className="showingCount">
          {showingCount} of {props.totalItems || 0}
        </div>
      )}
      {props.totalItems !== 0 && (
        <div className="pagination">
          <div className="previous" onClick={previousPage}>
            <i
              className="bi bi-chevron-left"
              style={{
                color: `${isDarkModeOn ? "#fff" : "blue-300"}`,
                opacity: `${currentPage === 1 ? 0.5 : 1}`,
                fontSize: "14px",
                fontWeight: "700",
              }}
            ></i>
          </div>
          {paginationRange && (
            <ul className="list">
              {paginationRange.map((pageNumber, index) => {
                if (pageNumber === DOTS) {
                  return (
                    <li
                      className="page-number p-small-90 md:p-med-90"
                      onClick={() => {
                        if (index > 1) {
                          onPageChange(currentPage + 2);
                        } else {
                          onPageChange(currentPage - 2);
                        }
                      }}
                      key={index}
                    >
                      {DOTS}
                    </li>
                  );
                }
                return (
                  <li
                    className={`page-number p-med-90 ${
                      pageNumber === currentPage ? "active" : ""
                    }`}
                    onClick={() => onPageChange(pageNumber)}
                    key={index}
                  >
                    {pageNumber}
                  </li>
                );
              })}
            </ul>
          )}
          <div className="forward" onClick={nextPage}>
            <i
              className="bi bi-chevron-right"
              style={{
                color: `${isDarkModeOn ? "#fff" : "blue-300"}`,
                opacity: `${currentPage === totalPageCount ? 0.5 : 1}`,
                fontSize: "14px",
                fontWeight: "bold",
              }}
            ></i>
          </div>
        </div>
      )}
    </PaginationRoot>
  );
};
return <TablePagination {...props} />;

