    case "medium":
      return typographyClasses["p-small-90"];
    case "small":
      return typographyClasses["cap-big-90"];
  }
};
return {
  cssColors,
  colors,
  typographyClasses,
  getFontType,
  getCharsCounterSize,
  getInputLabelFontType,
};

