const OWNER_ID = "minorityprogrammers.near";
let accountId = context.accountId;
const DEFAULT_NFT_TITLE = "My awesome NFT";
const DEFAULT_NFT_DESCRIPTION = "I've just created an NFT using GenaDrop";
State.init({
  title: DEFAULT_NFT_TITLE,
  description: DEFAULT_NFT_DESCRIPTION,
  recipient: "",
  isSoulBound: false,
  showAlert: false,
  toastMessage: "",
  selectIsOpen: false,
  selectedChain: "0",
  customRecipient: false,
  mintedNfts: [],
});
if (state.sdk.initialized) {
  setTimeout(() => {
    State.update({ mintedNfts: state.sdk.getMintedNfts() });
  }, 1000);
}
const handleMint = () => {
  if (!state.image.cid) {
    return;
  }
  if (!state.title) {
    State.update({
      showAlert: true,
      toastMessage: "Please enter a title for the NFT",
    });
    setTimeout(() => {
      State.update({
        showAlert: false,
      });
    }, 3000);
  } else if (!state.description) {
    State.update({
      showAlert: true,
      toastMessage: "Please enter a description for the NFT",
    });
    setTimeout(() => {
      State.update({
        showAlert: false,
      });
    }, 3000);
  } else {
    try {
      state.sdk.mint(
        state.recipient,
        state.title,
        state.description,
        state.selectedChain,
        state.image.cid,
        state.isSoulBound
      );
    } catch (error) {
      console.log(error);
    }
  }
};
if (state.sender === undefined) {
  const accounts = Ethers.send("eth_requestAccounts", []);
  if (accounts.length) {
    State.update({ sender: accounts[0] });
    Ethers.provider()
      .getNetwork()
      .then((data) => {
        State.update({
          selectedChain: data.chainId,
        });
      });
  }
}
const handleSelectClick = () => {
  State.update({
    selectIsOpen: !state.selectIsOpen,
  });
};
const handleOutsideClick = (e) => {
  if (!!state.selectIsOpen) {
    State.update({
      selectIsOpen: false,
    });
  }
};
const onChangeTitle = (title) => {
  State.update({
    title,
  });
};
const onChangeRecipient = (recipient) => {
  State.update({
    customRecipient: true,
  });
  if (state.selectedChain == "0") {
    State.update({
      recipient: recipient[0],
    });
  } else {
    State.update({
      recipient,
    });
  }
};
const handleChainChange = (chain_id) => {
  try {
    Ethers.send("wallet_switchEthereumChain", [
      { chainId: `0x${Number(chain_id).toString(16)}` },
    ]);
    State.update({
      selectedChain: chain_id,
    });
  } catch (err) {
    console.log(err);
  }
};
const onChangeDesc = (description) => {
  State.update({
    description,
  });
};
const handleToggle = () => {
  State.update({
    isSoulBound: !state.isSoulBound,
  });
};
