  }
  Object.keys(policy.users)?.map((item) => {
    const index = voters.findIndex((d) => d.account === item);
    if (index === -1) {
      voters.push({
        account: item,
        groups: policy.users?.[item],
        approve: 0,
        rejected: 0,
        isHuman: fetchIsHuman(item),
