        if (accountIndex !== -1) {
          voters[accountIndex] = {
            ...voters[accountIndex],
            rejected: voters[accountIndex].rejected + voterData.rejected,
            approve: voters[accountIndex].approve + voterData.approve,
          };
        } else {
          voters.push({
            ...voterData,
            isHuman: fetchIsHuman(voterData.account),
            isUserFollowed: fetchIsUserFollowed(voterData.account),
          });
        }
      });
    });
