      break;
    case CoADaoId:
      group = "CoA Member";
      break;
    case TCDaoId:
      group = "Transparency Commission Member";
      break;
  }
  const obj = {
    policy,
    users: {},
    roles: {
      [group]: {
        permissions: members?.permissions,
      },
    },
    everyone: {},
  };
  members?.members?.map((item) => {
    obj.users[item] = [group];
  });
  return obj;
}
