  const timeA = a[1]?.voting_end_time || 0;
  const timeB = b[1]?.voting_end_time || 0;
  // Sort in descending order (latest first)
  return timeB - timeA;
};
useEffect(() => {
  let sortedContests = [...fetchedContests];
  switch (activeTab) {
    case "ALL":
