  const response2 = fetch("https://graph.mintbase.xyz/mainnet", {
    method: "POST",
    headers: {
      "mb-api-key": "anon",
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      query: `query MyQuery {
  mb_views_nft_tokens(
    where: {nft_contract: {id: {_eq: "marmaj.mintbase1.near"}}}
    offset: 0
    limit: 3
  ) {
                media 
                owner
                token_id
                nft_contract_id
                description
                title
                listings {
                    price
                    unlisted_at
                    listed_by

            }
      }
}
`,
    }),
  });
  if (response2.ok) {
