  return_type: "BlockHeight",
});
if (!data) return "";
function convertToNestedObject(obj) {
  const result = {};
  Object.keys(obj)
    .filter((key) => key !== "index")
    .forEach((key) => {
      let parts = key.split(".");
      let firstPart = parts.shift();
      if (parts[1] === "demo") {
        return;
      }
      if (parts.length) {
        result[firstPart] = result[firstPart] || {};
        result[firstPart][parts.join(".")] = obj[key];
      } else {
        result[firstPart] = obj[key];
      }
    });
  return result;
}
